export class SlideMetricsComponent {
  @ViewChild('chartContainer') chartContainer!: ElementRef;

  constructor() {
    afterNextRender(() => {
      this.initChart();
    });
  }

  initChart() {
    const element = this.chartContainer.nativeElement;
    // Clear previous
    d3.select(element).selectAll('*').remove();

    const margin = {top: 20, right: 30, bottom: 30, left: 40};
    const width = element.clientWidth - margin.left - margin.right;
    const height = element.clientHeight - margin.top - margin.bottom;

    const svg = d3.select(element)
      .append('svg')
      .attr('width', '100%')
      .attr('height', '100%')
      .attr('viewBox', `0 0 ${element.clientWidth} ${element.clientHeight}`)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Data: Q1-Q4 projections for 2 years
    const data = [
      { date: new Date(2024, 0, 1), value: 4000 },
      { date: new Date(2024, 3, 1), value: 4800 },
      { date: new Date(2024, 6, 1), value: 6200 },
      { date: new Date(2024, 9, 1), value: 7500 },
      { date: new Date(2025, 0, 1), value: 9000 },
      { date: new Date(2025, 3, 1), value: 11500 },
      { date: new Date(2025, 6, 1), value: 14000 },
      { date: new Date(2025, 9, 1), value: 18000 },
    ];

    const x = d3.scaleTime()
      .domain(d3.extent(data, (d: any) => d.date))
      .range([0, width]);

    const y = d3.scaleLinear()
      .domain([0, 20000])
      .range([height, 0]);

    // Area
    svg.append("path")
      .datum(data)
      .attr("fill", "url(#gradient)")
      .attr("d", d3.area()
        .x((d: any) => x(d.date))
        .y0(height)
        .y1((d: any) => y(d.value))
        .curve(d3.curveMonotoneX)
      );

    // Line
    svg.append("path")
      .datum(data)
      .attr("fill", "none")
      .attr("stroke", "#3B5BDB")
      .attr("stroke-width", 3)
      .attr("d", d3.line()
        .x((d: any) => x(d.date))
        .y((d: any) => y(d.value))
        .curve(d3.curveMonotoneX)
      );

    // Gradient
    const defs = svg.append("defs");
    const gradient = defs.append("linearGradient")
      .attr("id", "gradient")
      .attr("x1", "0%")
      .attr("y1", "0%")
      .attr("x2", "0%")
      .attr("y2", "100%");

    gradient.append("stop")
      .attr("offset", "0%")
      .attr("stop-color", "#3B5BDB")
      .attr("stop-opacity", 0.4);

    gradient.append("stop")
      .attr("offset", "100%")
      .attr("stop-color", "#3B5BDB")
      .attr("stop-opacity", 0);

    // X Axis
    svg.append("g")
      .attr("transform", `translate(0,${height})`)
      .call(d3.axisBottom(x).ticks(5).tickSize(0).tickPadding(10))
      .select(".domain").remove();

    // Y Axis (custom grid)
    svg.append("g")
      .call(d3.axisLeft(y).ticks(5).tickSize(-width).tickPadding(10))
      .select(".domain").remove();

    // Style grid lines
    svg.selectAll(".tick line")
      .attr("stroke", "#E5E7EB")
      .attr("stroke-dasharray", "4");

    svg.selectAll(".tick text")
      .attr("fill", "#9CA3AF")
      .style("font-family", "Inter")
      .style("font-size", "11px");

    // Add dots
    svg.selectAll(".dot")
      .data(data)
      .enter().append("circle") 
      .attr("class", "dot") 
      .attr("cx", (d: any) => x(d.date))
      .attr("cy", (d: any) => y(d.value))
      .attr("r", 5)
      .attr("fill", "white")
      .attr("stroke", "#3B5BDB")
      .attr("stroke-width", 2);
  }
}

